//package com.example.alp_vp_frontend.ui.view\n\nimport androidx.compose.material.icons.Icons\nimport androidx.compose.material.icons.filled.CalendarToday\nimport androidx.compose.material.icons.filled.Home\nimport androidx.compose.material.icons.filled.Logout\nimport androidx.compose.material.icons.filled.MonetizationOn\nimport androidx.compose.material.icons.filled.Timer\nimport androidx.compose.material3.Icon\nimport androidx.compose.material3.NavigationBar\nimport androidx.compose.material3.NavigationBarItem\nimport androidx.compose.material3.Text\nimport androidx.compose.runtime.Composable\nimport androidx.compose.runtime.getValue\nimport androidx.compose.ui.graphics.vector.ImageVector\nimport androidx.navigation.NavController\nimport androidx.navigation.compose.currentBackStackEntryAsState\nimport com.example.alp_vp_frontend.ui.route.Route\n\ndata class NavItem(\n    val label: String,\n    val icon: ImageVector,\n    val route: String\n)\n\n@Composable\nfun BottomNavBar(navController: NavController) {\n    val navItems = listOf(\n        NavItem(\"Home\", Icons.Default.Home, Route.Home.screenName),\n        NavItem(\"Finance\", Icons.Default.MonetizationOn, Route.Finance.screenName),\n        NavItem(\"Calendar\", Icons.Default.CalendarToday, Route.ActivityCalendar.screenName),\n        NavItem(\"Timer\", Icons.Default.Timer, Route.Timer.screenName),\n        NavItem(\"Logout\", Icons.Default.Logout, Route.Logout.screenName)\n    )\n\n    NavigationBar {\n        val navBackStackEntry by navController.currentBackStackEntryAsState()\n        val currentRoute = navBackStackEntry?.destination?.route\n\n        navItems.forEach { item ->\n            NavigationBarItem(\n                icon = { Icon(item.icon, contentDescription = item.label) },\n                label = { Text(item.label) },\n                selected = currentRoute == item.route,\n                onClick = {\n                    navController.navigate(item.route) {\n                        // Pop up to the start destination of the graph to\n                        // avoid building up a large stack of destinations\n                        // on the back stack as users select items\n                        navController.graph.startDestinationRoute?.let {\ route ->\n                            popUpTo(route) {\n                                saveState = true\n                            }\n                        }\n                        // Avoid multiple copies of the same destination when\n                        // reselecting the same item\n                        launchSingleTop = true\n                        // Restore state when reselecting a previously selected item\n                        restoreState = true\n                    }\n                }\n            )\n        }\n    }\n}\n